import enum, datetime

class ProductType(enum.Enum):
    Raw = 0,
    Food = 1,
    Wood = 2,
    Metall = 3

class OperationType(enum.Enum):
    Buy = 0,
    Sell = 1

class Product():
    def __init__(self, name, initCount):
        self.name = name
        self.count = initCount
    
    def equals(product1, product2):
        return product1.name == product2.name
    
    def add(self, count):
        self.count = count

class Company():
    def __init__(self, name):
        self.companyName = name
        self.dateCreate = datetime.date.today()
        self.products = []
        self.money = 1000.00
    
    def addProduct(self, addProduct):
        for product in self.products:
            if Product.equals(addProduct, product):
                product.add(addProduct.count)
                return
        self.products.append(addProduct)
    
    def find(self, addProduct):
        for product in self.products:
            if Product.equals(addProduct, product):
                return product
        return False
            

class Operation():
    def __init__(self, company, operationType):
        self.company = company
        self.operationType = operationType

