from PyQt6.QtCore import QSize, Qt
from PyQt6.QtWidgets import *

import data

class MenuWindow():
    def __init__(self, controller):
        pass

    def switch(self, controller):
        pass

class OperationsWindow():
    def __init__(self, controller):
        pass

    def switch(self, controller):
        pass

class ReportsWindow():
    def __init__(self, controller):
        self.layout = QVBoxLayout()
        self.createButton("Операции", self.layout, controller.switchOperations)
        self.createButton("Отчёты", self.layout, controller.switchReports)

    def switch(self, controller):
        controller.setWindowTitle("Главное меню")
        controller.setFixedSize(QSize(500, 300))
        controller.setLayout(self.layout)
    
    def createButton(self, name, layout, func):
        button = QPushButton(name)
        button.setFixedSize(QSize(300, 100))
        button.clicked.connect(func)
        layout.addWidget(button)

# Подкласс QMainWindow для настройки главного окна приложения
class WindowController(QMainWindow):
    def __init__(self):
        super().__init__()
        
        self.menu = MenuWindow(self)
        self.operations = OperationsWindow(self)
        self.reports = ReportsWindow(self)

        self.switchMenu()

    def switchMenu(self):
        self.menu.switch(self)

    def switchOperations(self):
        self.operations.switch(self)

    def switchReports(self):
        self.reports.switch(self)